mapper.select:

	select gNum,gName,gPrice,gMainImg,gMainDetail,
  	gImg,gDetail,gCount,gStock,cNum from shop_Good
	where gNum=#{gNum}


select gNum,gName,gPrice,gMainImg,gMainDetail,
gImg,gDetail,gCount,gStock,cNum from shop_Good
where cNum=#{cNum}

mapper.update:

update shop_Good set gName=#{gName},
gPrice=#{gPrice},gMainImg=#{gMainImg},gMainDetail=#{gMainDetail},
gImg=#{gImg},gDetail=#{gDetail},gStock=#{gStock},cNum=#{cNum}
where gNum=#{gNum}
